    <style>
        /* === Pop-up admin === */
        .popup-overlay {
            display: none !important;
            position: fixed;
            inset: 0;
            background: rgba(0, 0, 0, 0.8);
            z-index: 9999;
            justify-content: center;
            align-items: center;
        }

        .popup-overlay.active {
            display: flex !important; /* <— visible quand la classe active est ajoutée */
        }

        .popup-content {
            position: relative;
            background: transparent;
            border-radius: 10px;
            max-width: 600px;
            width: 90%;
            text-align: center;
        }

        .popup-content img,
        .popup-content video {
            width: 100%;
            border-radius: 10px;
        }

        .close-btn {
            position: absolute;
            top: 10px;
            right: 15px;
            font-size: 22px;
            color: white;
            cursor: pointer;
        }
        /* ====== DASHBOARD ====== */
        :root {
            --bg: #f4f6f7;
            --card: #ffffff;
            --teal: #2f4b50;
            --accent: #16a085;
            --muted: #98a4a6;
        }

        .page-wrap {
            padding: 24px;
            max-width: 1280px;
            margin: 0 auto 40px;
        }

        .dash-header {
            background: var(--teal);
            color: white;
            padding: 22px 28px;
            border-left: 6px solid #19b38f;
            border-radius: 4px;
            box-shadow: 0 1px 0 rgba(0, 0, 0, 0.05);
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin-bottom: 18px;
        }

        .dash-header h1 {
            margin: 0;
            font-size: 20px;
            letter-spacing: 1px;
        }

        .grid {
            display: grid;
            grid-template-columns: repeat(12, 1fr);
            gap: 18px;
        }

        .card {
            background: var(--card);
            border-radius: 6px;
            box-shadow: 0 6px 18px rgba(23, 31, 33, 0.04);
            padding: 18px;
            min-height: 120px;
        }

        .kpi {
            grid-column: span 3;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
        }

        .kpi .value {
            font-size: 34px;
            font-weight: 700;
            color: #263238;
        }

        .muted {
            color: var(--muted);
            font-size: 13px;
        }

        .main-chart {
            grid-column: span 9;
            padding: 18px;
        }

        .bar-chart {
            display: flex;
            align-items: flex-end;
            justify-content: space-between;
            height: 220px;
            margin-top: 10px;
        }

        .bar {
            width: 40px;
            background: #19b38f;
            border-radius: 4px 4px 0 0;
            display: flex;
            align-items: flex-end;
            justify-content: center;
            position: relative;
        }

        .bar::after {
            content: attr(data-value);
            position: absolute;
            top: -25px;
            color: #333;
            font-size: 12px;
        }

        .bar-label {
            text-align: center;
            font-size: 12px;
            color: var(--muted);
            margin-top: 6px;
        }

        .pie-wrap {
            grid-column: span 3;
            text-align: center;
        }

        .pie {
            width: 160px;
            height: 160px;
            border-radius: 50%;
            background: conic-gradient(#19b38f 0 52%, #e06a63 0 100%);
            display: flex;
            align-items: center;
            justify-content: center;
            color: #fff;
            font-weight: 700;
            margin: 0 auto;
        }

        /* ====== FOOTER ====== */
        footer {
            background-color: #004b84;
            color: white;
            text-align: center;
            padding: 20px;
            font-size: 14px;
            margin-top: 40px;
        }

        /* ====== RESPONSIVE ====== */
        @media (max-width: 980px) {
            .grid {
                grid-template-columns: repeat(6, 1fr);
            }

            .kpi {
                grid-column: span 6;
            }

            .main-chart {
                grid-column: span 6;
            }

            .pie-wrap {
                grid-column: span 6;
            }
        }

        @media (max-width: 520px) {
            .grid {
                grid-template-columns: 1fr;
            }

            .kpi .value {
                font-size: 26px;
            }

            .bar {
                width: 25px;
            }
        }
    </style>
