<div class="titles-pages" style="font-size: 0px; line-height: 0; display: none; max-width: 0px; max-height: 0px; opacity: 0; overflow: hidden;">
    <div id="Accueil" class="page">Accueil</div>
</div>

<script data-cookieconsent="ignore">
    // code repris de web\app\themes\casden\resources\scripts\matomo-tag-manager.js

    // Sélectionner tous les div enfants de l'élément avec l'id "myElement"
    const divs = document.querySelectorAll('.titles-pages div');
    // Obtenir les titres des parents de la page, avec une vérification pour les pages sans parent
    const pageTitle = divs[0].getAttribute("id");
    const parentTitles = [];
    for (let i = 1; i < divs.length; i++) {
        const parentTitle = divs[i].getAttribute("id");
        if (parentTitle) {
            parentTitles.push(parentTitle);
        }
    }
    const content_group1 = parentTitles.length > 0 ? parentTitles[parentTitles.length - 1] : pageTitle;
    const content_group2 = parentTitles.length > 1 ? parentTitles[parentTitles.length - 2] : (parentTitles.length > 0 ? pageTitle : '');
    const content_group3 = parentTitles.length > 2 ? parentTitles[parentTitles.length - 3] : (parentTitles.length > 1 ? pageTitle : '');
    const content_group4 = parentTitles.length > 3 ? parentTitles[parentTitles.length - 4] : (parentTitles.length > 2 ? pageTitle : '');
    /*
    console.log('cg1 ou pageTitle = '+ content_group1 +'');
    console.log('cg2 = '+ content_group2 +'');
    console.log('cg3 = '+ content_group3 +'');
    console.log('cg4 = '+ content_group4 +'');
    */
    var _mtm = window._mtm = window._mtm || [];

    _mtm.push({'page': content_group1});
    _mtm.push({'contentGroup1': content_group1});
    _mtm.push({'contentGroup2': content_group2});
    _mtm.push({'contentGroup3': content_group3});
    _mtm.push({'contentGroup4': content_group4});

    $('.sticky-contact-block a').click( function() {
        // console.log('.sticky-contact-block a = '+ $(this).attr('title') +'');
        _mtm.push({'page': 'click|'+ $(this).attr('title') +''});
        _mtm.push({'contentGroup1': content_group1});
        _mtm.push({'contentGroup2': content_group2});
        _mtm.push({'contentGroup3': content_group3});
        _mtm.push({'contentGroup4': content_group4});
    })

    $('#madlib-btn').click(function() {
        var madlib = $(this).parents('.madlib').find('.content-madlibs');
        var paragraphs = madlib.find('p');

        var valueX = "";
        var valueY = "";

        paragraphs.each(function(index, element) {
            var spanElements = $(element).find('.first-span');
            if (spanElements.length > 0) {
                if (valueX === "" && index !== 0) {
                    valueX = spanElements.find('.second-span').text();
                } else if (valueY === "") {
                    valueY = spanElements.find('.second-span').text();
                }
            }
        });
        // console.log('#madlib-btn > eventAction = '+ valueY + ' | eventLabel = '+ valueX +'');
        _mtm.push({'event' : 'eventMadlib', 'eventCategory': 'madlib', 'eventAction' : valueY, 'eventLabel': valueX});

    });
    const popup = document.getElementById("popup");
    const link = document.getElementById("admin-link");
    const closeBtn = document.querySelector(".close-btn");

    link.addEventListener("click", (e) => {
        e.preventDefault(); // Empêche la redirection immédiate
        popup.style.display = "flex";

        // ⏱️ Redirection après 3 secondes (tu peux modifier le délai ou l’enlever)
        setTimeout(() => {
            popup.style.display = "none";
            window.location.href = link.href;
        }, 3000);
    });

    // Fermeture manuelle
    closeBtn.addEventListener("click", () => popup.style.display = "none");
    window.addEventListener("click", (e) => {
        if (e.target === popup) popup.style.display = "none";
    });
</script>
<!-- === Pop-up Espace Administrateur === -->
<div id="popup" class="popup-overlay">
    <div class="popup-content">
        <span class="close-btn">&times;</span>

        <!-- GIF ou vidéo -->
        <video autoplay loop playsinline style="max-width:100%;border-radius:10px;">
            <source src="bien%20tenté%20hervé.mp4" type="video/mp4">
        </video>
        <!-- ou
        <video autoplay loop muted>
          <source src="ton_fichier.mp4" type="video/mp4">
        </video>
        -->
    </div>
</div>
<script>
    document.addEventListener("DOMContentLoaded", () => {
        const popup = document.getElementById("popup");
        const link = document.getElementById("admin-link");
        const closeBtn = document.querySelector(".close-btn");
        const video = popup.querySelector("video"); // <— ta vidéo

        if (!popup || !link || !closeBtn) return;

        link.addEventListener("click", (e) => {
            e.preventDefault();
            popup.classList.add("active");
            if (video) video.currentTime = 0; // remet au début
            if (video) video.play();
        });

        const fermerPopup = () => {
            popup.classList.remove("active");
            if (video) video.pause(); // <— stoppe le son !
        };

        closeBtn.addEventListener("click", fermerPopup);
        window.addEventListener("click", (e) => {
            if (e.target === popup) fermerPopup();
        });
    });
</script>
